import React, { useRef, useEffect, useState } from 'react'
// import AudioPlayer from "./audio/AudioPlayer";


//Data fields
const inc = 5



const getPixelRatio = context => {
  var backingStore =
  context.backingStorePixelRatio ||
  context.webkitBackingStorePixelRatio ||
  context.mozBackingStorePixelRatio ||
  context.msBackingStorePixelRatio ||
  context.oBackingStorePixelRatio ||
  context.backingStorePixelRatio ||
  1;

  return (window.devicePixelRatio || 1) / backingStore;
};


//sound  
const convertHexToRGBA = (hexCode, opacity) => {
  // console.log("="* 20)
  // console.log(opacity)
  const hex = hexCode.replace('#', ''); 

  const r = parseInt(hex.substring(0, 2), 16);
  const g = parseInt(hex.substring(2, 4), 16);
  const b = parseInt(hex.substring(4, 6), 16);

  return `rgba(${r},${g},${b},${opacity / 100})`;
};




//start of function
const Canvas = props=> {
  const { keycode, setKey, allData, setAllData, scale } = props
  const [Datas, setDatas] = useState(allData[scale]);
  const canvasRef = useRef(null)



  const draw = (ctx, frameCount) => {
    let new_data = {}
    Object.keys(allData).forEach(ind => {
      if (ind == scale){
        for (let i = 0; i < allData[ind].length; i++){
          let item = allData[ind][i]
          if (item.key.localeCompare(keycode) == 0){
            item.opa += inc
            item.col_display = convertHexToRGBA(item.col_origin, item.opa)

          }
          else{
            continue;
          }

        }
        // console.log()
        new_data[ind] = allData[ind]
        // console.log(allData[ind])
     }else{

       new_data[ind] = allData[ind]

     }
    })

    setAllData(new_data)
    // console.log(new_data)


    Object.entries(allData).forEach(entry => {
      const [key, value] = entry;
      value.forEach((item, index) => {
        ctx.beginPath(); 
        ctx.lineWidth = 20;
        // console.log("change")
        // console.log(item.opa)
        ctx.strokeStyle = item.col_display
        let count = 12
        // console.log(item.col_display)
        let part = 2*Math.PI / count
        let start = index* part
        let radius = key* 60
        ctx.arc(350, 350, radius, start, part+start)
        ctx.stroke()

      })
    });

    // Object.entries(chords).forEach(entry => {
    //   const [key, value] = entry;
    //   value.forEach((item, index) => {
    //     ctx.beginPath(); 
    //     ctx.lineWidth = 15;
    //     // console.log("change")
    //     // console.log(item.opa)
    //     ctx.strokeStyle = item.col_display
    //     let count = 12
    //     // console.log(item.col_display)
    //     let part = 2*Math.PI / count
    //     let start = index* part
    //     let radius = key* 76 - (key-1) * 16
    //     ctx.arc(350, 350, radius, start, part+start)
    //     ctx.stroke()

    //   })
    // });


    setKey('')

  }


  //   useEffect(() => {
  //   audioPlayer.setInstrument("acoustic_grand_piano");
  // }, []);
  // useEffect(() =>{ 

  //   Datas.forEach((item, index) => {
  //     // let snd  = new Audio();
  //     // src  = document.createElement("source");
  //     // src.type = "audio/wav";
  //     let mus = item.pitch
  //     console.log(item.pitch)
  //     // if (mus.includes("#")){
  //     //   let ind = mus.indexOf("#")
  //     //   mus = mus.slice(0,ind) +"%23" +mus.slice(ind+1,mus.length)
  //     // }
  //     // src.src  = "guitar/Crispy Clean Strat/Crispy Clean Strat Samples/Cripsy Clean Strat - "+muc+".wav"
  //     // snd.appendChild(src);
  //   })


  // }, [])



  useEffect(() => {

    const canvas = canvasRef.current
    const context = canvas.getContext('2d')
    let frameCount = 0
    let animationFrameId



    /////////
    let ratio = getPixelRatio(context);
    let width = window.getComputedStyle(canvas)
        .getPropertyValue('width')
        .slice(0, -2);
    let height = getComputedStyle(canvas)
        .getPropertyValue('height')
       .slice(0, -2);
    // console.log(width, height)
    canvas.width = width * ratio;
    canvas.height = height * ratio;
    canvas.style.width = `${width}px`;
    canvas.style.height = `${height}px`;



    //////////
    draw(context, frameCount)
    // audioPlayer.playNote("C4")
    // const render = () => {
    //   frameCount++
    //   draw(context, frameCount, keycode)
    //   animationFrameId = window.requestAnimationFrame(render)
    // }
    // render()

    // return () => {
    //   window.cancelAnimationFrame(animationFrameId)
    // }
  }, [keycode])

  return <canvas ref={canvasRef} style={{ width: window.innerWidth, height: window.innerHeigh}}/>
}

export default Canvas
